import {Component, OnInit, Input, Output, EventEmitter} from '@angular/core';
import {Item} from '../models/item';
import {FormGroup, FormControl, Validators} from '@angular/forms';
import {Observable} from 'rxjs';
import {ItemService} from '../services/item.service';
import {ItemListComponent} from '../item-list/item-list.component';
import {DatePipe} from '@angular/common';

@Component({
  selector: 'app-item-view',
  templateUrl: './item-view.component.html',
  styleUrls: ['./item-view.component.scss']
})

export class ItemViewComponent implements OnInit {
  @Input() itemListComponent: ItemListComponent;
  @Input() item: Item;

  private itemForm: FormGroup;
  // private title;
  // private content;
  // private date_modified;

  constructor(private datePipe: DatePipe) {
    this.itemForm = new FormGroup({
      title: new FormControl('', Validators.required),
      content: new FormControl('', Validators.required),
      date_modified: new FormControl(),
      date_created: new FormControl()
    });

  }

  ngOnInit() {
    this.showDetails();
    console.log(this.datePipe.transform(new Date(), 'yyyy-MM-dd\'T\'HH:mm:ss.SSS\'Z\''));
  }

  showDetails() {
    this.itemForm.patchValue({
      title: this.item.title,
      content: this.item.content,
      date_modified: this.datePipe.transform(this.item.modifiedDate, 'yyyy-MM-dd\'T\'HH:mm:ss.SSS\'Z\''),
      date_created: this.item.createdDate
    });
  }

  updateItemInfo() {
    if (confirm('Are you sure you want to update this item?')) {
      const title = this.itemForm.get('title').value;
      const content = this.itemForm.get('content').value;
      const modified_date = new Date();
      this.itemListComponent.updateItem(this.item.id, title, content, modified_date);
      this.itemForm.patchValue({
        date_modified: this.datePipe.transform(modified_date, 'yyyy-MM-dd\'T\'HH:mm:ss.SSS\'Z\'')
      });
    } else {
      return;
    }
  }
}

export class Item {
    id: string;
    title: string;
    content: string;
    createdDate: Date;
    modifiedDate: Date;
    constructor(title: string, content: string) {
        this.title = title;
        this.content = content;
        this.createdDate = new Date();
        this.modifiedDate = new Date();
    }
}

import {Injectable} from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import {Item} from '../models/item';

import {environment} from '../../environments/environment';
import {Observable} from 'rxjs';

@Injectable()
export class ItemService {

  itemResource: string;
  itemResourceURL: string;

  /**
   * Constructor.
   */
  constructor(private http: HttpClient) {
    this.itemResource = 'api';
    this.itemResourceURL = `${environment.serverBaseURL}/${this.itemResource}`;
  }

  /**
   * Returns all items.
   *
   * @return {Observable<Array<Item>>} {Observable sticky array of stickies}
   */
  getItems(): Observable<Array<Item>> {
    return this.http.get<Array<Item>>(`${this.itemResourceURL}/items`);
  }

  /**
   * Creates new item.
   *
   * @param  {Item} sticky: Sticky {new sticky object}
   * @return {Observable<Item>} {Observable for saved sticky object}
   */
  createItem(item: Item = null): Observable<Item> {
    let newItem: Item;
    newItem = item ? item : new Item('Untitled Item', '');
    return this.http.post<Item>(`${this.itemResourceURL}/item`, newItem);
  }

  updateItem(id: string, title: string, content: string, modifiedDate: Date) {
    const observable = this.http.put<Item>(`${this.itemResourceURL}/item/${id}`,
      {title: title, content: content, modifiedDate: modifiedDate});
    observable.subscribe(res => {
      console.log('Update Item');
      console.log(res);
    });
  }

  deleteItem(id: string) {
    const observable = this.http.delete<Item>(`${this.itemResourceURL}/item/${id}`);
    observable.subscribe(res => {
      console.log('Delete Item');
      console.log(res);
    });
  }
}
